$(document).mouseover(function(){
	if ($('#captcha').val() == '') {
		$('#contact').attr('disabled', 'disabled');
	}
	if ($('#q_captcha').val() == '') {
		$('#quick_query').attr('disabled', 'disabled');
	}
});
$(document).ready(function() { 
	$("#captcha").keyup(function(){
    	var num1 = $('#num1').val();
    	var num2 = $('#num2').val();
    	var sumation = parseInt(num1, 10) + parseInt(num2, 10);

    	var captcha = $('#captcha').val();

    	if (captcha == sumation) {
    		$('#contact').removeAttr('disabled', 'disabled');
    	} else {
    		$('#contact').attr('disabled', 'disabled');
    	}
	});
	$("#q_captcha").keyup(function(){
    	var q_num1 = $('#q_num1').val();
    	var q_num2 = $('#q_num2').val();
    	var q_sumation = parseInt(q_num1, 10) + parseInt(q_num2, 10);

    	var q_captcha = $('#q_captcha').val();

    	if (q_captcha == q_sumation) {
    		$('#quick_query').removeAttr('disabled', 'disabled');
    	} else {
    		$('#quick_query').attr('disabled', 'disabled');
    	}
	});

var inputLocalFont = document.getElementById("userImage");
inputLocalFont.addEventListener("change",previewImages,false);
	function previewImages(){
	$('#targetLayer').html("");

    var fileList = this.files;
    var totalfile=fileList.length;
		if(totalfile>3){
			alert("Your Total file exceed. Maximum 3 file you can upload.");
			$('#userImage').val('');
			return false;
		}
    var anyWindow = window.URL || window.webkitURL;
        var maxfullsize=47185920;
		 var maxsize    = 15728640;
		var allsize=0
		for(var i = 0; i < fileList.length; i++){
			allsize=allsize+fileList[i].size;
			var single =fileList[i].size;
		if(single>=maxsize){
				alert("File too large. File must be less than 15 megabytes");
				$('#userImage').val('');
				return false;
			}
		}
		if(allsize>=maxfullsize){
			alert("All Image Size Must be Less Then 45MB!!!");
			$('#userImage').val('');
			return false;
		}
		
        for(var i = 0; i < fileList.length; i++){
          var objectUrl = anyWindow.createObjectURL(fileList[i]);
		  
		  var getsize=fileList[i].size/1024;
		  if(getsize<1024){
			  var actual=getsize.toFixed(2)+" KB";
		  }
		  else{
			  sizem=getsize/1024;
			  var actual=sizem.toFixed(2)+" MB";
		  }
		  var tblm='<table role="presentation" style="margin-bottom:0;" class="table table-bordered table-hover"><tbody id="allfiles_table" class="files"><tr class="template-upload fade show"><td style="width:8%;"><img src="'+objectUrl+'" width="60px" height="60px" /></td><td style="width:77%;"><p class="name">'+fileList[i].name+'</p></td><td style="width:15%;"><p class="size">'+actual+'</p></td></tr></tbody></table>';
          $('#targetLayer').append(tblm);
          //console.log(fileList[i]);
          window.URL.revokeObjectURL(fileList[i]);
        }
}
    $('#uploadForm').submit(function(e) {	
        if($('#userImage').val()) {
            e.preventDefault();
            $('#onprogr').show();
			$('#progress-div').show();
            $(this).ajaxSubmit({ 
                target:   '#targetLayer', 
                beforeSubmit: function() {
                    $("#progress-bar").width('0%');
                },
                uploadProgress: function (event, position, total, percentComplete){	
                    $("#progress-bar").width(percentComplete + '%');
                    $("#progress-bar").html('<div id="progress-status">' + percentComplete +' %</div>')
                },
                success:function (){
                	$('#uploadForm')[0].reset();
                    $('#progress-div').hide();
					$('#onprogr').hide();
					$("#onprogr2").show();
                },
                resetForm: false 
            }); 
            return false; 
        }
		else{
			 e.preventDefault();
			 alert("You can't select Any Files");
			}
    });

});
	
	</script>
    <script>
			function file_count(){
				if(document.getElementById('files_table').rows.length > 3){
					alert('Only 3 files allowed');
					document.getElementById('files_table').innerHTML = '';
				}
			}
			function button_hover(){
				document.getElementsByName('btn_job_submit')[0].style.background = 'green';
			}
			function button_out(){
				document.getElementsByName('btn_job_submit')[0].style.background = '#4DAE4D';
			}
			function file_dir(){
				var name = $("#fld_name").val();
				var dater = "<?php echo gmstrftime("%d_%m_%Y_%H.%M.%S");?>";
				var histor = dater+"_"+name;
                                  //$.session.set('myVar', histor); 
				document.getElementsByName('txt_file_dir')[0].value = histor;
			}
			function upload_check(){
				document.getElementsByName('txt_check')[0].value = '1';
			}
		</script>
